mapper.insert:
insert into permission (PId, PName, PType
  )
values (#{pid,jdbcType=CHAR}, #{pname,jdbcType=VARCHAR}, #{ptype,jdbcType=INTEGER}
  )

mapper.update:
update permission
set PName = #{pname,jdbcType=VARCHAR},
  PType = #{ptype,jdbcType=INTEGER}
where PId = #{pid,jdbcType=CHAR}

mapper.select:
select PId, PName, PType
from permission
where PId = #{pid,jdbcType=CHAR}
